import React,{useState} from "react";
import MaskedInput from "react-text-mask";
import { Link, useLocation, useNavigate } from "react-router-dom";
import Header from "../../components/Header/Header";
import Footer from "../../components/Footer";
import Input from "../../components/Input";
import Button from "../../components/Button";
import pay from "../../images/mastercard.png"
import card from "../../images/carts.png"
import s from "./CheckOut.module.scss";
// function cc_format(value) {
//   const v = value
//     .replace(/\s+/g, "")
//     .replace(/[^0-9]/gi, "")
//     .substr(0, 16);
//   const parts = [];

//   for (let i = 0; i < v.length; i += 4) {
//     parts.push(v.substr(i, 4));
//   }

//   return parts.length > 1 ? parts.join(" ") : value;
// }



function CheckOut() {
  const reducer = (state, action) => {
    console.log("action", action.data);
    switch (action.type) {
      case "card":
        return { ...state, card: action.data };
      case "expiry":
        return { ...state, expiry: action.data };
      case "securityCode":
        return { ...state, securityCode: action.data };
      case "cardHodler":
        return { ...state, cardHodler: action.data };
      case "cleanState":
        return { ...action.data };
      default:
        return state;
    }
  };
  const regexPattern = {
    MASTERCARD: /^5[1-5][0-9]{1,}|^2[2-7][0-9]{1,}$/,
    VISA: /^4[0-9]{2,}$/,
    AMERICAN_EXPRESS: /^3[47][0-9]{5,}$/,
    DISCOVER: /^6(?:011|5[0-9]{2})[0-9]{3,}$/,
    DINERS_CLUB: /^3(?:0[0-5]|[68][0-9])[0-9]{4,}$/,
    JCB: /^(?:2131|1800|35[0-9]{3})[0-9]{3,}$/
  };
  for (const card in regexPattern) {
    if (cardNumber.replace(/[^\d]/g, "").match(regexPattern[card])) return card;
  }
  return "";

const handleInputData = (e) => {
  dispatch({ type: e.target.name, data: e.target.value });
};
  return (
    <>
      <Header />
      <form
        className={s.passengerForm}
        // onSubmit={onPassengerForm}
      >
        <div className={s.main}>
          <h2 className={s.info}>Passenger information:</h2>
          <div className={s.name_group}>
            <label> Name:</label>
            <Input type="text" name="passenger_name" placeholder="John" />
          </div>
          <div className={s.surname_group}>
            <label> Surname:</label>
            <Input type="text" name="passenger_surname" placeholder="Walker" />
          </div>
          <div className={s.email_group}>
            <label> Email:</label>
            <Input
              type="email"
              name="passenger_email"
              placeholder="john.walker@email.com"
            />
          </div>
          <div className={s.phone_group}>
            <label>Phone number:</label>
            <Input
              type="tel"
              name="passenger_phone"
              placeholder="498-348-3872"
            />
          </div>
          <div className={s.birthday_group}>
            <label>Birthday date:</label>
            <Input
              type="date"
              name="passenger_birthday"
              placeholder="year month date"
            />
          </div>
          <div className={s.passport_group}>
            <label>Passport Number:</label>
            <Input
              type="passport"
              name="passenger_passport"
              placeholder="N31195855"
            />
          </div>
          {/* <Button className={s.submit}>Submit</Button> */}
          <div className={s.cardBlock}>
          <figure className={s.fig}>
            <img src={card} alt="card" className={s.card}/>
            <figcaption className={s.figcaption}>
                <input type="text" placeholder="Holder of card" className={s.holder} />
                <input type="number" 
                placeholder="XXXX-XXXX-XXXX-XXXX" className={s.numberCard}
                />


                <p className={s.valid}>VALID THRU</p>
                <input type="text" placeholder="MM" className={s.month}  name="expiry-data"
/>
                <input type="text" placeholder="YY" className={s.year} />
                <img src={pay} alt="pay_method" className={s.payMethod} />
                <input type="number" name="cvc" placeholder="CVC" className={s.cvc} />
            </figcaption>
            </figure>
          </div>
        </div>

        <div className={s.priceBox}>
      <h2 className={s.priceTotal}>Total price:</h2>
      <h2 className={s.priceCurrency}>1190$</h2>
      </div>
        <div className={s.buttonBlock}>
        <Link to={`/success`} relative="path">
          <button className={s.checkOutButton}>Check out</button>
          </Link>
        </div>
      </form>
      <Footer />
    </>
  );
}
export default CheckOut;
