import React, { useState, useEffect, useMemo } from "react";
import { useDispatch, useSelector } from "react-redux";
import Button from "../../components/Button";
import { logOut } from "../../store/userSlice";
import { Link } from "react-router-dom";
// import { createAsyncThunk } from "@reduxjs/toolkit";
import logo from "../../images/companyLogo.png";
import userLogin from "../../images/userLogin.png";
import burgerMenu from "../../images/burgerMenu.png";
import banner from "../../images/banner.png";
import best from "../../images/bestflight.png";
import cheapest from "../../images/cheapestflight.png";
import direct from "../../images/directflight.png";
import explore from "../../images/explore.png";
import DirectionRoute from "../DirectionRoute";
import LinkCard from "../../components/LinkCard";
import Footer from "../../components/Footer";
import Cards from "../../pages/Cards"
import s from "./MainPage.module.scss";
import axios from "axios";
const endpoint =
  "https://skyscanner44.p.rapidapi.com/fly-to-country?destination=";

function MainPage() {
  const { user } = useSelector((state) => state.user);

  const dispatch = useDispatch();

  const onLogOut = () => {
    dispatch(logOut())
  }

  const [destination, setDestination] = useState("");
  const [origin, setOrigin] = useState("");
  const [date, setDate] = useState("");
  const [currency, setCurrency] = useState("");
  const [adults, setAdults] = useState("");
  const [response, setResponse] = useState("");
  const endpoint = "https://skyscanner44.p.rapidapi.com/search?";

  // const city = useMemo(()=> response?.data?.[0]?.itineraries?.buckets?.[0]?.items?.[0]?.legs?.[0].origin?.city)

  const onAdultChange = (event) => {
    setAdults(event.target.value);
  };
  const onOriginChange = (event) => {
    setOrigin(event.target.value);
  };

  const onDestinationChange = (event) => {
    setDestination(event.target.value);
  };
  const onDateChange = (event) => {
    setDate(event.target.value);
  };
  const onCurrencyChange = (event) => {
    setCurrency(event.target.value);
  };
  const onResponseChange = (event) => {
    setResponse(event.target.value);
  }
  const onButtonPress = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch(
        `${endpoint}adults=${adults}&origin=${origin}&destination=${destination}&departureDate=${date}&currency=${currency}`,
        {
          method: "GET",
          headers: {
            "X-RapidAPI-Key":
              "abc749408emsh80d4b185487f4f7p12bfb3jsn1ef71c820ebf",
            "X-RapidAPI-Host": "skyscanner44.p.rapidapi.com",
            "Content-Type": "application/json",
          },
        }
      );
      const data = await response.json();
       setResponse(data.itineraries.buckets);

      console.log(data.itineraries.buckets);
      // console.log(data.itineraries.buckets[0].items[0]);
      // console.log(data.itineraries.buckets[0].items[0].legs[0].origin.city);
      // console.log(data.itineraries.buckets[0].items[0].legs[0].departure);
      // console.log(data.itineraries.buckets[0].items[0].legs[0].destination.city);
      // console.log(data.itineraries.buckets[0].items[0].legs[0].durationInMinutes);
      // console.log(data.itineraries.buckets[0].items[0].legs[0].arrival);
      // console.log(data.itineraries.buckets[0].items[0].price.formatted);
      console.log(data.itineraries.buckets[0].items[1].legs[0].segments[0].destination.name);
      // console.log(data)
        //  return data.itineraries.buckets;
    } catch (error) {
      return error;
    }
    // const onButtonPress = () => <DirectionRoute/>;
    // const onButtonPressMemorize = React.memo(onButtonPress);
  };

  const city = useMemo(()=> response?.[0]?.data?.[0]?.itineraries?.buckets?.[0]?.items?.[0]?.legs?.[0].origin?.city [response])
  // console.log(response);

  useMemo(() => {
    setResponse();

  }, [])
  useEffect(() => {
    setResponse();
    console.log(response);
    // return <DirectionRoute/>
  }, [])


  // const city = useMemo(()=> response?.data?.[0]?.itineraries?.buckets?.[0]?.items?.[0]?.legs?.[0].origin?.city[response])


  // const resultData = useMemo(()=> response?.data?.[0]?.itineraries?.buckets)
  return (<>
    <header className={s.wrapper}>
      <div className={s.headerInner}>
        <div className={s.logoBlock}>
          <img src={logo} alt="logo" className={s.companyLogo} />
        </div>

        <div className={s.authBlock}>
          <img src={userLogin} alt="user" className={s.userLogin} />
          <p className={s.loginText}>My account</p>
          <img src={burgerMenu} alt="menu" className={s.burgerMenu} />
          <button onClick={onLogOut} className={s.logOut}>LogOut</button>
        </div>
      </div>

      <h2 className={s.introText}>
        Quickly scan all your favorite travel sites
      </h2>

      <form className={s.flightSection} onSubmit={onButtonPress}>
        <div className={s.inputs}>
          <span className={s.fromText}>From</span>
          <input
            type="text"
            className={s.fromLocation}
            value={origin}
            onChange={onOriginChange}
            placeholder="Los Angeles"
          />

          <span className={s.toText}>To</span>
          <input
            type="text"
            className={s.toDestination}
            value={destination}
            onChange={onDestinationChange}
            placeholder="Country,city or airport"
          />

          <span className={s.departText}>Depart</span>
          <input
            type="text"
            className={s.departDate}
            value={date}
            onChange={onDateChange}
