import React, { useState, useEffect } from "react";
import { cardsData } from "../../data/cardsData";
import s from "./PlacesLink.module.scss";
import { Link, useNavigate, useLocation } from "react-router-dom";


function PlacesLink({ image, title }) {

  const[response, setResponse] = useState();

  const fetchSerp = async (query) => {
    const apiKey = '08055d42b197991a23c1c937accab7c631deffc59e10db04c83327dc104833cc'; // Замените YOUR_API_KEY на ваш ключ API SerpApi

    const proxyUrl = 'https://cors-anywhere.herokuapp.com/'; // Пример URL прокси-сервера
    const apiUrl = `https://serpapi.com/search.json?q=${encodeURIComponent(query)}&key=${apiKey}`; 
    try {
      const response = await fetch(proxyUrl + apiUrl);
      const data = await response.json();
      return data;
    } catch (error) {
      console.log(`Произошла ошибка при выполнении запроса: ${error}`);
    }
  };

  // Пример использования
  const searchQuery = 'Italy Destinations';
  fetchSerp(searchQuery)
    .then((serpData) => {
      console.log(serpData);
    });


  return (
    <>
      <div className={s.BpkCard}>
        <div className={s.BpkImage}>
          <img src={image} className={s.image} />
        </div>
        <div className={s.InternalLinkCard}>
          <div className={s.InternalLinkTitle}>{title}</div>
          <div className={s.priceTitle}>

            <button className={s.buttonSelect} 
            // onChange={onButtonPress}

            >Read</button>
          </div>
        </div>
      </div>
    </>
  );
}
export default PlacesLink;
