import React, { useState } from "react";
import { cardsData } from "../../data/cardsData";
import s from "./PlacesLink.module.scss";
import { Link, useNavigate, useLocation } from "react-router-dom";
const endpoint="https://serpapi.com/search.json?engine=google&q=";
function PlacesLink({ image, title }) {

  const[response, setResponse] = useState();
  // const [title, setTitle] = useState();
  const [destination, setDestination] = useState();

  const onButtonPress = async (e) => {
    e.preventDefault();
  //   navigate(
  //     `/direction?adults=${adults}&origin=${origin}&destination=${destination}&departureDate=${date}&currency=${currency}`
  //   );
    try {
      const response = await fetch(
        `${endpoint}${title}&destination=${destination}`,
        {
          method: "GET",
          headers: {
            "X-RapidAPI-Key":
              "c5b7ce6d59msh628ea192fa8b5ffp186318jsnfbe20ac3d2c2",
            "X-RapidAPI-Host": "skyscanner44.p.rapidapi.com",
            "Content-Type": "application/json",
          },
        }
      );
      const data = await response.json();
      setResponse(data);

    } catch (error) {
      return error;
    }
  };

  return (
    <>
      <div className={s.BpkCard}>
        <div className={s.BpkImage}>
          <img src={image} className={s.image} />
        </div>
        <div className={s.InternalLinkCard}>
          <div className={s.InternalLinkTitle}>{title}</div>
          <div className={s.priceTitle}>

            <button className={s.buttonSelect} onChange={onButtonPress}>Read</button>
          </div>
        </div>
      </div>
    </>
  );
}
export default PlacesLink;
