        <div className={s.priceBox}>
      <h2 className={s.priceTotal}>Total price:</h2>
      <h2 className={s.priceCurrency}>1190$</h2>
      </div>
        <div className={s.buttonBlock}>
        <Link to={`/success`} relative="path">
