import React,{useState} from "react";
import { Link, useLocation, useNavigate } from "react-router-dom";
import Header from "../../components/Header/Header";
import Footer from "../../components/Footer";
import Input from "../../components/Input";
import Button from "../../components/Button";
import pay from "../../images/mastercard.png"
import card from "../../images/carts.png"
import s from "./CheckOut.module.scss";

function CheckOut() {
  // const sendEmail = (e) => {
  //   e.preventDefault();

  //   emailjs.sendForm('gmail', 'template_9u11duf', e.target, 'H421ulPJCcC_lNGBp')
  //     .then((result) => {
  //         console.log(result.text);
  //     }, (error) => {
  //         console.log(error.text);
  //     });
  // };

const [card, setCard] = useState({
cardno: "",
cardtype: "far fa-credit-card",
expirydt: ""
});

const onChange = (e) => {
var cartype_new = cardnumber(e.target.value);
setCard({
  ...card,
  cardno: e.target.value,
  cardtype: cartype_new
});
};
const cardnumber = (inputtxt) => {
var matches = inputtxt.match(/(\d+)/);
var cardno = "";
console.log(matches);
if (matches) {
  cardno = inputtxt.split(" - ").join("");
}
console.log(cardno);
var cardtype1 = card.cardtype;
//var visa = /^(?:4[0-9]{16}(?:[0-9]{3})?)$/;
var visa = /^(?:4[0-9]{2}?)$/;
var mastercardRegEx = /^(?:5[1-5][0-9]{3})$/;
var amexpRegEx = /^(?:3[47][0-9]{3})$/;
var discovRegEx = /^(?:6(?:011|5[0-9][0-9])[0-9]{5})$/;
console.log(visa.test(cardno));
if (visa.test(cardno) === true) {
  //eg:4651970022334445
  cardtype1 = "far fa fa-3x fa-cc-visa  carddetail-cardtype";
} else if (mastercardRegEx.test(cardno) === true) {
  cardtype1 = "far fa fa-3x fa-cc-mastercard carddetail-cardtype";
} else if (amexpRegEx.test(cardno) === true) {
  cardtype1 = "far fa fa-3x fa-cc-amex carddetail-cardtype";
} else if (discovRegEx.test(cardno) === true) {
  cardtype1 = "far fa fa-3x fa-cc-discover carddetail-cardtype";
}
return cardtype1;
};

const cc_format = (value) => {
const v = value.replace(/[^0-9]/gi, "").substr(0, 16);

const parts = [];
for (let i = 0; i < v.length; i += 4) {
  parts.push(v.substr(i, 4));
}
return parts.length > 1 ? parts.join(" - ") : value;
};
const expriy_format = (value) => {
const expdate = value;
const expDateFormatter =
  expdate.replace(/\//g, "").substring(0, 2) +
  (expdate.length > 2 ? "/" : "") +
  expdate.replace(/\//g, "").substring(2, 4);

return expDateFormatter;
};
const onChangeExp = (e) => {
setCard({
  ...card,
  expirydt: e.target.value
});
};
  return (
    <>
      <Header />
      <form
        className={s.passengerForm}
        // onSubmit={onPassengerForm}
      >
        <div className={s.main}>
          <h2 className={s.info}>Passenger information:</h2>
          <div className={s.name_group}>
            <label> Name:</label>
            <Input type="text" name="passenger_name" placeholder="John" />
          </div>
          <div className={s.surname_group}>
            <label> Surname:</label>
            <Input type="text" name="passenger_surname" placeholder="Walker" />
          </div>
          <div className={s.email_group}>
            <label> Email:</label>
            <Input
              type="email"
              name="passenger_email"
              placeholder="john.walker@email.com"
            />
          </div>
          <div className={s.phone_group}>
            <label>Phone number:</label>
            <Input
              type="tel"
              name="passenger_phone"
              placeholder="498-348-3872"
            />
          </div>
          <div className={s.birthday_group}>
            <label>Birthday date:</label>
            <Input
              type="date"
              name="passenger_birthday"
              placeholder="year month date"
            />
          </div>
          <div className={s.passport_group}>
            <label>Passport Number:</label>
            <Input
              type="passport"
              name="passenger_passport"
              placeholder="N31195855"
            />
          </div>
          {/* <Button className={s.submit}>Submit</Button> */}
          <div className={s.cardBlock}>
          <figure className={s.fig}>
            <img src={card} alt="card" className={s.card}/>
            <figcaption className={s.figcaption}>
                <input type="text" placeholder="Holder of card" className={s.holder} />
                <input type="text"  className={s.numberCard}
                 data-mask="0000 0000 0000 0000"
                 placeholder="XXXX-XXXX-XXXX-XXXX"
                 value={cc_format(card.cardno)}
                 onChange={onChange}
                 onKeyPress={(event) => {
                   if (!/[0-9]/.test(event.key)) {
                     event.preventDefault();
                   }
                 }}
                />

                <p className={s.valid}>VALID THRU</p>
                <input type="text" placeholder="MM" className={s.month} />
                <input type="text" placeholder="YY" className={s.year} />
                <img src={pay} alt="pay_method" className={s.payMethod} />
                <input type="number" name="cvc" placeholder="CVC" className={s.cvc} />
            </figcaption>
            </figure>
          </div>
        </div>

        <div className={s.priceBox}>
      <h2 className={s.priceTotal}>Total price:</h2>
      <h2 className={s.priceCurrency}>1190$</h2>
      </div>
        <div className={s.buttonBlock}>
        <Link to={`/success`} relative="path">
          <button className={s.checkOutButton}>Check out</button>
          </Link>
        </div>
      </form>
      <Footer />
    </>
  );
}
export default CheckOut;
